SELECT ROUND(CONVERT(SUM(money),DECIMAL(15,4)),2) money FROM (SELECT a.id aid, a.zone_id zoneid, af.id afid, af.money, af.subject_id, af.voucher_id, a.start_date, a.end_date, a.`status`, a.last_operating_surplus, a.last_pledge_surplus, s.* FROM accounts a, accounts_financialinform af, accounts_subject s, accounts_voucher av
af.subject_id=s.id AND af.voucher_id=av.id AND av.accounts_id=a.id
AND a.id=#{id}
AND s.type=#{type}
AND s.rdtype=#{rdtype}
AND ( (a.start_date = #{accountsDate} AND a.end_date is null) OR (a.start_date <= #{accountsDate} AND #{accountsDate} <= a.end_date) )
AND YEAR(a.end_date)= #{yearDate}
AND a.zone_id=#{zoneId}
AND a.status=#{status}
ORDER BY a.end_date DESC )aa

SELECT ROUND(CONVERT(ss.surplus +bb.money,DECIMAL(15,4)),2) AS money FROM ( SELECT ROUND(
last_operating_surplus
last_pledge_surplus
last_operating_surplus + last_pledge_surplus
,2)AS surplus FROM accounts a
AND a.zone_id=#{zoneId}
AND a.status=#{status}
ORDER BY a.start_date ASC LIMIT 1
) ss, ( SELECT CASE WHEN money IS NULL THEN 0 ELSE money END AS money FROM( SELECT ROUND((sum(case rdtype when 1 then money else 0 end))-(sum(case rdtype when 2 then money else 0 end)),2) AS money FROM (SELECT a.id aid, af.id afid, af.money, af.subject_id, af.voucher_id, a.start_date, a.end_date, a.`status`, a.last_operating_surplus, a.last_pledge_surplus, s.* FROM accounts a, accounts_financialinform af, accounts_subject s, accounts_voucher av
af.subject_id=s.id AND af.voucher_id=av.id AND av.accounts_id=a.id
AND s.type=#{type}
AND ( (a.start_date = #{accountsDate} AND a.end_date is null) OR (a.end_date <=#{accountsDate}) )
AND YEAR(a.end_date) <= #{yearDate}
AND a.zone_id=#{zoneId}
AND a.status=#{status}
)aa )cc )bb

SELECT ss.*, ROUND(CONVERT(dd.money,DECIMAL(15,4)),2) money,dd.NAME FROM( SELECT CASE WHEN a2.id IS NOT NULL THEN a2.id ELSE a1.id END AS id, a1.`name` pname, a1.rdtype, a1.type, a1.scount, a2.`name` sname FROM ( SELECT dd.*, qq.scount FROM ( SELECT * FROM accounts_subject
parent_id = '0'
AND type=#{type}
AND rdtype=#{rdtype}
) dd LEFT JOIN ( SELECT count(*) scount, parent_id FROM accounts_subject WHERE parent_id > '0' GROUP BY parent_id ) qq ON qq.parent_id = dd.id ) a1 LEFT JOIN accounts_subject a2 ON a1.id = a2.parent_id ORDER BY id ) ss LEFT JOIN ( SELECT id, NAME, SUM(money) money FROM ( SELECT a.id aid, af.id afid, af.money, af.subject_id, af.voucher_id, a.start_date, a.end_date, a.`status`, a.last_operating_surplus, a.last_pledge_surplus, s.* FROM accounts a, accounts_financialinform af, accounts_subject s, accounts_voucher av
af.subject_id=s.id AND af.voucher_id=av.id AND av.accounts_id=a.id
AND s.type=#{type}
AND s.rdtype=#{rdtype}
AND ( (a.start_date = #{accountsDate} AND a.end_date is null) OR (a.start_date <= #{accountsDate} AND #{accountsDate} <= a.end_date) )
AND YEAR(a.end_date)= #{yearDate}
AND a.zone_id=#{zoneId}
AND a.status=#{status}
ORDER BY s.id ) aa GROUP BY id, NAME ) dd ON ss.id = dd.id ORDER BY ss.id,dd.NAME

SELECT a.id aid, af.id , af.money, af.subject_id, af.voucher_id, af.auxiliary, af.digest, a.start_date, a.end_date, a.`status`, av.date, s.name, s.type, s.rdtype FROM accounts a, accounts_financialinform af, accounts_subject s, accounts_voucher av
af.subject_id=s.id AND af.voucher_id=av.id AND av.accounts_id=a.id
AND s.type=#{type}
AND s.rdtype=#{rdtype}
AND ( (a.start_date = #{accountsDate} AND a.end_date is null) OR (a.start_date <= #{accountsDate} AND #{accountsDate} <= a.end_date) )
AND YEAR(a.end_date)= #{yearDate}
AND a.zone_id=#{zoneId}
AND a.status=#{status}
AND s.id=#{subjectId}
ORDER BY a.create_time

