SELECT af.id , af.money, af.subject_id, af.auxiliary, af.digest, a.auditor, av.tag_number tagNumber, av.make_username makeUsername, av.date FROM accounts_financialinform af, accounts a, accounts_voucher av
af.voucher_id=av.id AND av.accounts_id=a.id AND a.status='3'
AND af.subject_id = #{subjectId}
AND af.digest like '%${digest}%'
AND af.auxiliary like '%${auxiliary}%'
AND av.voucher_id = #{voucherId}
AND av.date >= #{startDate}
AND av.date <= #{endDate}
AND a.zone_id=#{zoneId}
AND av.tag_number=#{tagNumber}
